import { Component } from '@angular/core';

class Guest{
  constructor(
    public firstname: string,
    public lastname: string,
    public email: string,
    public phone: string,

  ){ }
}

class Booking{
  constructor(
    public guest: Guest,
  ){ }
}

@Component({
  selector: 'app-bookings',
  templateUrl: './bookings.component.html',
  styleUrls: ['./bookings.component.css']
})
export class BookingsComponent {
    public guest: Guest;
    public booking: Booking;

    constructor() {
        this.guest = new Guest('', '', '', '');
        this.booking = new Booking(this.guest);
    }
}
<h2>Creating a booking</h2>

<p>Do some check to see if the guest already is in our database (by their email?)</p>
<form #checkform="ngForm">
    <div>
        <label>Email
            <input type="text" />
        </label>
    </div>
</form>

<h3>Enter details for guest</h3>

<form #guestForm="ngForm">
    <fieldset>
        <legend>First Name</legend>
        <input type="text" id="fname" name="firstname" [(ngModel)]="guest.firstname" size="40">
    </fieldset>
    <fieldset>
        <legend>Last Name</legend>
        <input type="text" id="lname" name="lastname" [(ngModel)]="guest.lastname" size="40">
    </fieldset>
    <fieldset>
        <legend>Email</legend>
        <input type="text" id="email" name="email" [(ngModel)]="guest.email" size="40">
    </fieldset>
    <fieldset>
        <legend>Phone Number</legend>
        <input type="text" id="phone" name="phone" [(ngModel)]="guest.phone" size="40">
    </fieldset>
    <input type="submit" value="submit" name="b1">
</form>




<h3>Enter details for booking</h3>

<form #bookingForm="ngForm">
    <div>
        <label>etcetcetcetcc
            <input type="text">
        </label>
    </div>
    <div>
        <label>Check-in Date and Time
            <input type="datetime-local" id="checkIn"
            name="checkin" value=""
            min="2023-08-01T00:00" max="2023-10-01T00:00">
        </label>
    </div>
    <div>
        <label>Check-out Date and Time
            <input type="datetime-local" id="checkOut"
            name="checkout" value=""
            min="2023-08-01T00:00" max="2023-10-01T00:00">
        </label>
    </div>

</form>
<p>etc etc etc</p>
