import { Component } from '@angular/core';
import { HotelAPIserviceService } from '../hotel-apiservice.service';

class Guest{
    constructor(
    public firstName: string,
    public lastName: string,
    public email: string,
    public phone: string,

  ){ }
}

interface IGuest {
    id: number;
    firstName: string;
    lastName: string;
    email: string;
    phone: string;
}

class Room {

    constructor(
        public id: number,
        public roomNo: number,
        public roomType: number,
        public pricePerNight: number,
    ) { }

}


class Booking{
  constructor(
      public guestId: number,
      public startDate: string,
      public endDate: string,
      public room: Room,
      public roomId: number,
      public roomNo: number,
      public guest: Guest,
      public totalPrice: number,
      public partySize: number
  ){ }
}

@Component({
  selector: 'app-bookings',
  templateUrl: './bookings.component.html',
  styleUrls: ['./bookings.component.css'],
})
export class BookingsComponent {
    public guest: Guest;
    public booking: Booking;
    public room: Room;
    public rooms: Room[] = [];
    public guests: IGuest[] = [];

    constructor(private http: HotelAPIserviceService) {
        this.guest = new Guest('', '', '', '');
        this.room = new Room(0,0,0,0);
        this.booking = new Booking(0, '', '', this.room, 0, 0, this.guest, 0, 0);
        this.getAllRooms();
        this.getAllGuests();
    }

    public submitBooking() {
        for (let i = 0; i < this.rooms.length; i++) {
            if (this.rooms[i].roomNo === this.booking.roomNo) {
                this.booking.roomId = this.rooms[i].id;

                //to-do   add logic to calculate no. of nights
                this.booking.totalPrice = this.rooms[i].pricePerNight;
            }
        }
        for (let i = 0; i < this.guests.length; i++) {
            if (this.guests[i].id === this.booking.guestId) {
                this.booking.guest = this.guests[i];
            }
        }

        this.http.addNewBooking(JSON.parse(JSON.stringify(this.booking)));
        this.booking = new Booking(0, '', '', this.room, 0, 0, this.guest, 0, 0);
    }

    public getAllRooms() {
        this.http.getAllBookings().subscribe(response => {
            this.rooms = response;
        })
    }

    public getAllGuests() {
        this.http.getAllGuests().subscribe(response => {
            this.guests = response;
        })
    }

}
<h2>Booking Creation</h2>






<h3>Enter details for booking</h3>

<form (ngSubmit)="submitBooking()" #bookingForm="ngForm">
    <fieldset>
        <legend>Guest ID</legend>
        <input type="number" id="guestID" name="guestID" [(ngModel)]="booking.guestId" required>
    </fieldset>
    <fieldset>
        <legend>Check-in Date and Time</legend>
        <input type="datetime-local" id="checkIn" [(ngModel)]="booking.startDate"
               name="checkin" value=""
               min="2023-08-01T00:00" max="2023-10-01T00:00" required>
    </fieldset>
    <fieldset>
        <legend>Check-out Date and Time</legend>
        <input type="datetime-local" id="checkOut" [(ngModel)]="booking.endDate"
               name="checkout" value=""
               min="2023-08-01T00:00" max="2023-10-01T00:00" required>
    </fieldset>
    <fieldset>
        <legend>Room Number</legend>
        <input type="number" id="roomNo" name="roomNo" [(ngModel)]="booking.roomNo" required>
    </fieldset>
    <fieldset>
        <legend>Party Size</legend>
        <input type="number" id="partySize" name="partySize" [(ngModel)]="booking.partySize" required>
    </fieldset>
    <button type="submit" id="bookingButton" [disabled]="!bookingForm.form.valid">Submit</button>
</form>

<p>{{ booking | json }}</p>
