import { Component } from '@angular/core';
import { FormControl } from '@angular/forms';

class Guest{
  constructor(
    public firstname: string,
    public lastname: string,
    public email: string,
    public phone: string,

  ){ }
}

class Booking{
  constructor(
    public guest: Guest,
  ){ }
}

@Component({
  selector: 'app-bookings',
  templateUrl: './bookings.component.html',
  styleUrls: ['./bookings.component.css']
})
export class BookingsComponent {
    public guest: Guest;
    public booking: Booking;

    constructor() {
        this.guest = new Guest('', '', '', '');
        this.booking = new Booking(this.guest);
    }

    public submitBooking() {
        alert("Booking button clicked")
    }

    public submitGuest() {
        alert("Guest button clicked")
    }
}
<h2>Creating a booking</h2>

<p>Do some check to see if the guest already is in our database (by their email?)</p>
<form #checkform="ngForm">
    <div>
        <label>Email
            <input type="text" />
        </label>
    </div>
</form>

<h3>Enter details for guest</h3>

<form #guestForm="ngForm">
    <fieldset>
        <legend>First Name</legend>
        <input type="text" id="fname" name="firstname" [(ngModel)]="guest.firstname" min="2">
    </fieldset>
    <fieldset>
        <legend>Last Name</legend>
        <input type="text" id="lname" name="lastname" [(ngModel)]="guest.lastname">
    </fieldset>
    <fieldset>
        <legend>Email</legend>
        <input type="text" id="email" name="email" [(ngModel)]="guest.email">
    </fieldset>
    <fieldset>
        <legend>Phone Number</legend>
        <input type="text" id="phone" name="phone" [(ngModel)]="guest.phone">
    </fieldset>
    <button (click)="submitGuest()" id="guestButton" [disabled]="!guestForm.form.valid">Submit</button>
</form>




<h3>Enter details for booking</h3>

<form #bookingForm="ngForm">
    <fieldset>
        <legend>etc etc</legend>
        <input type="text">
    </fieldset>

    <fieldset>
        <legend>Check-in Date and Time</legend>
        <input type="datetime-local" id="checkIn"
               name="checkin" value=""
               min="2023-08-01T00:00" max="2023-10-01T00:00">
    </fieldset>

    <fieldset>
        <legend>Check-out Date and Time</legend>
        <input type="datetime-local" id="checkOut"
               name="checkout" value=""
               min="2023-08-01T00:00" max="2023-10-01T00:00">
    </fieldset>
    <button (click)="submitBooking()" id="bookingButton" [disabled]="!bookingForm.form.valid">Submit</button>
</form>

<p>etc etc etc</p>
